import React, { Component } from 'react';
import axios from 'axios';

var mylink1='http://localhost:4000/';
var errMsg_mylink1='issue with server check >>'+mylink1+'\n';
var c=0;

class HomePage extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            today:'',
            hour:'',
            minutes:'',
            seconds:'',
            currentTime:''
        };
    }
        updateTime(){
            setInterval(()=>{
                axios.get("/stime")
                .then(response => {
                    c+=1;
                    console.log(c+":"+response);
                    this.setState({
                        today: response.data.today,
                        hour: response.data.hour,
                        minutes: response.data.minutes,
                        seconds: response.data.seconds,
                        currentTime: response.data.hour+':'+response.data.minutes+':'+response.data.seconds
                    });                    
                })
                .catch(err => {
                    console.log(errMsg_mylink1+err.message);
                })
            },60000);
        }
        
        
 
        

    render() { 
    return ( <div className="myTime">
    <center>
    <h2>Date: {this.state.today}</h2> 
    <h2>Time: {this.state.currentTime}</h2>
    <sub>note:-time would update after every 1min</sub>
    </center>
    {this.updateTime()}
    </div> );
    }
}

export default HomePage;
