import React, { Component } from 'react';
import axios from 'axios';

var mylink1='http://localhost:4000/';
var errMsg_mylink1='issue with server check >>'+mylink1+'\n';

class HomePage extends Component {
    constructor(props) {
    super(props);
    this.state = { 
    today:'',
    hour:'',
    minutes:'',
    seconds:''
    };
    }
        updateTime(){
            axios.get(mylink1)
            .then(response => {
                console.log(response);
                this.setState({
                    today: response.data.today,
                    hour: response.data.hour,
                    minutes: response.data.minutes,
                    seconds: response.data.seconds
                });
            })
            .catch(err => {
                console.log(errMsg_mylink1+err.message);
            })
        }

    componentWillMount() {
        this.updateTime();
    }
        
    componentDidMount() {
        this.interval = setInterval(() => this.updateTime(), 60000);
    }
        
    componentWillUnmount(){
        clearInterval(this.interval);
    }        

    render() { 
    return ( <div className="myTime">
    <center>
    <h2>Date: {this.state.today}</h2> 
    <h2>Time: {this.state.hour+":"+this.state.minutes.toString()+":"+this.state.seconds.toString()}</h2>
    <sub>note:-time would update after every 1min</sub>
    </center>
    {this.updateTime()}
    </div> );
    }
}

export default HomePage;
