import React, { Component } from 'react';
import axios from 'axios';

var mylink1='http://localhost:4000';
var errMsg_mylink1='issue with server check >>'+mylink1+'\n';

class HomePage extends Component {
constructor(props) {
super(props);
this.state = { 
time:new Date(),
date:new Date()
};
}
updateTime(){
setInterval(()=>{
axios.get(mylink1)
.then(response=>{
console.log('updateTime(): Time when you hit url was::',new Date().toLocaleTimeString());
this.setState({time:new Date()});
})
.catch(function(error){
console.log(errMsg_mylink1+error);
});
}
,60000);
}

componentDidMount(){
axios.get(mylink1)
.then(response=>{
console.log('componentDidMount(): Time when you hit url was::',new Date().toLocaleTimeString());
this.setState({time:new Date()});
})
.catch(function(error){
console.log(errMsg_mylink1+error);
});
}

render() { 
return ( <div className="myTime">
<center>
<h2>Date: {this.state.date.toLocaleDateString()}</h2> 
<h2>Time: {this.state.time.toLocaleTimeString()}</h2>
<sub>note:-time would update after every 1min</sub>
</center>
{this.updateTime()}
</div> );
}
}

export default HomePage;
